import React, { useState } from "react";

import "./image.css";

const ImageData = [
  {
    name: "image",
    dateUploaded: "01-01-2023",
    url: "data:image/jpeg;base64,",
    fileSize: "10MB",
  },

  {
    name: "image",
    dateUploaded: "01-01-2023",
    url: "data:image/jpeg;base64,",
    fileSize: "5MB",
  },

  {
    name: "image",
    dateUploaded: "01-02-2023",
    url: "data:image/jpeg;base64,",
    fileSize: "15MB",
  },

  {
    name: "image",
    dateUploaded: "01-02-2023",
    url: "data:image/jpeg;base64,",
    fileSize: "10MB",
  },

  {
    name: "image",
    dateUploaded: "01-03-2023",
    url: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS92eisuWOx3tEjeW14mT9ACVgXDwIRBGtnww&usqp=CAU",
    fileSize: "5MB",
  },

  {
    name: "image",
    dateUploaded: "01-03-2023",
    url: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTExtoLVhMIfPRj_8d5RQKF2qjwUbuYL2tZTg&usqp=CAU",
    fileSize: "10MB",
  },
];

const ImageList = () => {
  const [view, setView] = useState("grid");
  const [sort, setSort] = useState(null);
  const [search, setSearch] = useState("");
  const [page, setPage] = useState(1);

  const pageSize = 5;

  const handleSort = (field) => {
    if (sort === field) {
      setSort(`${field}`);
    } else {
      setSort(field);
    }
  };
  const handleSearch = (e) => {
    setSearch(e.target.value);
    setPage(1);
  };

  const handlePageChange = (e) => {
    setPage(parseInt(e.target.textContent));
  };

  let filterdData = ImageData.filter((image) => {
    return (
      image.name.toLowerCase().includes(search.toLowerCase()) ||
      image.fileSize.includes(search)
    );
  });

  if (sort) {
    filterdData = filterdData.sort((a, b) => {
      if (sort.startsWith("-")) {
        return b[sort.slice(1)] > a[sort.slice(1)] ? 1 : -1;
      } else {
        return a[sort] > b[sort] ? 1 : -1;
      }
    });
  }

  const pageCount = Math.ceil(filterdData.length / pageSize);
  return (
    <>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Search"
          value={search}
          onChange={handleSearch}
        />
      </div>
      <div className="view-toggle">
        <button
          className={view === "grid" ? "active" : ""}
          onClick={() => setView("grid")}
        >
          Grid
        </button>
      </div>

      <button
        className={view === "list" ? "active" : ""}
        onClick={() => setView("list")}
      >
        List
      </button>

      <div className="sort">
        <button onClick={() => handleSort("name")}>Name</button>
        <button onClick={() => handleSort("dateUploaded")}>
          Date Uploaded
        </button>
        <button onClick={() => handleSort("fileSize")}>File Zise</button>
      </div>

      <div className={`images${view}`}>
        {filterdData
          .slice((page - 1) * pageSize, page * pageSize)
          .map((image, index) => (
            <div key={index} className="image">
              <img src={image.url} alt={image.name} />
              <div className="details">
                <div className="name">{image.name}</div>
                <div className="date">{image.dateUploaded}</div>
              </div>
              <div className="file-size">{image.fileSize}</div>
            </div>
          ))}
      </div>
      <div className="pagination">
        {Array.from({ length: pageCount }, (_, index) => (
          <button
            key={index}
            className={page === index + 1 ? "active" : ""}
            onAuxClick={handlePageChange}
          >
            {index + 1}
          </button>
        ))}
      </div>
    </>
  );
};

export default ImageList;
